plugin.callback:
/* float a = 87.7; */
float ainv = 0.011402508551881; /* 1.0 / a */
float aloginv = 0.182684374819744; /* 1.0 / (1.0 + log(a)) */
float aexp = 16.021419671691538; /* a * aloginv */
unsigned long pos;
for (pos = 0; pos < sample_count; pos++) {
    float s = input[pos];
    float sabs = fabs(s);
    if (sabs < ainv) {
        s *= aexp;
    } else {
        if (s >= 0)
             s = 1.0 + log(sabs) * aloginv;
        else
             s = -1.0 - log(sabs) * aloginv;
    }
    buffer_write(output[pos], s);
}
